package Interview;

public class SwapTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 40;
		
		int b = 30;
		
		a = a+b; // a = 70
		
		b = a- b; // b = 40
		
		a = a - b; // a =30
		
		System.out.println(a);
		System.out.println(b);
		
		

	}

}
